var local = (window.location.href.indexOf('localhost')>-1||window.location.href.indexOf('192.168.')>-1)?'http://localhost:3007':'https://api.ciding.cc';
function loadStyles(url) {
    var link = document.createElement("link");
    link.type = "text/css";
    link.rel = "stylesheet";
    link.href = url;
    document.getElementsByTagName("head")[0].appendChild(link);
}

function injectJs(jsPath,fn){
    var temp = document.createElement('script');
    temp.setAttribute('type', 'text/javascript');
    // 获得的地址类似：chrome-extension://ihcokhadfjfchaeagdoclpnjdiokfakg/js/inject.js
    temp.src = jsPath;
    temp.onload = function()
    {
      // 放在页面不好看，执行完后移除掉
      this.parentNode.removeChild(this);
      fn && fn();
    };
    document.head.appendChild(temp);
}



loadStyles(local+'/public/menu/menu.css')
if(typeof(jQuery) ==='undefined'){
    injectJs(local+'/public/menu/jquery.min.js',function(){
      injectJs(local+'/public/menu/menu.js');
    });
}else{
  injectJs(local+'/public/menu/menu.js');
}